import React from "react";
import { motion } from "framer-motion";
import truckImg from "../assets/HomeTruck.png"; // Make sure this path is correct
import SplitText from "./SplitText";

const handleAnimationComplete = () => {
  console.log('All letters have animated!');
};

const TruckIntro = () => {
  return (
    <>
      <style>{`
        .intro-container {
          position: relative;
          width: 100%;
          height: 300px;
          background-color: #f0f8ff;
          overflow: hidden;
          display: flex;
          align-items: center;
          justify-content: flex-start;
          padding-left: 2rem;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        .truck {
          position: absolute;
          bottom: 30px;
          width: 150px;
          z-index: 2;
        }

        .brand-text {
          font-size: 2.5rem;
          font-weight: bold;
          color: #1a1a1a;
          margin-left: 180px;
          white-space: nowrap;
          overflow: hidden;
          z-index: 1;
        }
      `}</style>

      <div className="intro-container">
        <motion.img
          src={truckImg}
          alt="Truck"
          className="truck"
          initial={{ x: "100vw" }}
          animate={{ x: "-20vw" }}
          transition={{ duration: 4, ease: "easeInOut" }}
        />

        <motion.h1
          className="brand-text"
          initial={{ width: 0, opacity: 0 }}
          animate={{ width: "auto", opacity: 1 }}
          transition={{ delay: 1.5, duration: 2.5, ease: "easeOut" }}
        >
         {/* --- Section Between Video and Image --- */}

                 <div className="relative w-full flex flex-row items-start justify-between">

                   {/* Left: Sledge logo and Software Solutions stacked */}
                   <div className="flex flex-col items-start justify-start ml-16" style={{ minWidth: "350px" }}>
                     <SplitText
                       text="Sledje"
                       className="text-7xl md:text-7xl py-2 font-light text-grey md:tracking-tight text-left text-spacing-loose"
                       delay={80}
                       duration={0.4}
                       ease="power3.out"
                       splitType="chars"
                       from={{ opacity: 0, y: 40 }}
                       to={{ opacity: 1, y: 0 }}
                       threshold={0.1}
                       rootMargin="-100px"
                       textAlign="left"
                       onLetterAnimationComplete={handleAnimationComplete}
                     />
                     <SplitText
                       text="Software Solutions"
                       className="text-1xl md:text-3xl font-light text-grey md:tracking-tight text-left"
                       delay={80}
                       duration={0.4}
                       ease="power3.out"
                       splitType="chars"
                       from={{ opacity: 0, y: 40 }}
                       to={{ opacity: 1, y: 0 }}
                       threshold={0.1}
                       rootMargin="-100px"
                       textAlign="left"
                       onLetterAnimationComplete={handleAnimationComplete}
                     />
                   </div>
                 </div>
        </motion.h1>
      </div>
    </>
  );
};

export default TruckIntro;
