import React, { useRef, useEffect, useState } from "react";
import { motion } from "framer-motion";
import truckImg from "../assets/HomeTruck.png";
import SplitText from "./SplitText";

const handleAnimationComplete = () => {
  console.log("All letters have animated!");
};

const TruckIntro = () => {
  const sectionRef = useRef(null);
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        setInView(entry.isIntersecting);
      },
      { threshold: 0.3 }
    );
    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <>
      <style>{`
        .truck-section {
          position: relative;
          width: 100%;
          min-height: 400px;
          background-color: #ffffff;
          overflow: hidden;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          padding: 2rem 1rem;
        }

        .truck {
          position: absolute;
          bottom: 40px;
          width: 150px;
          z-index: 2;
        }

        .text-container {
          position: relative;
          z-index: 1;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          text-align: center;
          margin-top: 2rem;
        }
      `}</style>

      <div className="truck-section" ref={sectionRef}>
        {/* Truck animation from left to right */}
        <motion.img
          src={truckImg}
          alt="Truck"
          className="truck"
          initial={{ x: "-1600px", opacity: 0 }} // Start further left
          animate={
            inView
              ? { x: "calc(100vw + 200px)", opacity: 1 } // Move fully off right
              : { x: "-200px", opacity: 0 }
          }
          transition={{ duration:7, ease: "easeInOut" }} // Slower animation
        />

        {/* Animated text fades in together with truck movement */}
        <motion.div
          className="text-container"
          initial={{ opacity: 0 }}
          animate={inView ? { opacity: 1 } : { opacity: 0 }}
          transition={{
            delay: 1.5, // No delay, starts with truck
            duration: 5, // Same as truck duration
            ease: "easeOut",
          }}
        >
          <SplitText
            text="Sledje"
            className="text-7xl md:text-7xl py-2 font-light text-black md:tracking-tight text-center"
            delay={80}
            duration={0.4}
            ease="power3.out"
            splitType="chars"
            from={{ opacity: 0, y: 40 }}
            to={{ opacity: 1, y: 0 }}
            onLetterAnimationComplete={handleAnimationComplete}
          />
          <SplitText
            text="Software Solutions"
            className="text-xl md:text-3xl font-light text-black md:tracking-tight text-center"
            delay={80}
            duration={0.4}
            ease="power3.out"
            splitType="chars"
            from={{ opacity: 0, y: 40 }}
            to={{ opacity: 1, y: 0 }}
            onLetterAnimationComplete={handleAnimationComplete}
          />
        </motion.div>
      </div>
    </>
  );
};

export default TruckIntro;
