import React, { useState, useEffect } from 'react';
import { User, Building2, Users, Mail, Lock, Phone, MapPin, FileText, Eye, EyeOff } from 'lucide-react';

export default function Login() {
  const [activeTab, setActiveTab] = useState('login');
  const [userType, setUserType] = useState('retailers');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [location, setLocation] = useState("");

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const coords = `${position.coords.latitude},${position.coords.longitude}`;
          setLocation(coords);
        },
        (error) => {
          console.error("Error fetching location:", error);
          alert("Please enable location services to auto-fill location.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser");
    }
  }, []);


  // Login form state
  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });

  // Registration form state
  const [registerData, setRegisterData] = useState({
    businessName: '',
    CompanyName: '',
    ownerName: '',
    email: '',
    password: '',
    phone: '',
    gstNumber: '',
    businessType: '',
    pincode: '',
    location: ''
  });

  const userTypes = [
    { id: 'retailers', label: 'Retailers', icon: Building2 },
    { id: 'distributors', label: 'Distributors', icon: Users },
    { id: 'partners', label: 'Partners', icon: User }
  ];

  const businessTypes = [
    'Retail Store', 'Wholesale', 'E-commerce', 'Manufacturing', 
    'Distribution', 'Service Provider', 'Other'
  ];

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setMessage({ type: 'success', text: 'Login successful' });
      console.log('Login attempt:', loginData);
    } catch (error) {
      setMessage({ type: 'error', text: 'Invalid credentials' });
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setMessage({ type: 'success', text: 'Account created successfully' });

      // Reset form data
      setRegisterData({
        businessName: '',
        CompanyName: '',
        ownerName: '',
        email: '',
        password: '',
        phone: '',
        gstNumber: '',
        businessType: '',
        pincode: '',
        location: ''
      });

      // Switch to login tab after successful registration
      setTimeout(() => {
        setActiveTab('login');
        setMessage({ type: '', text: '' });
      }, 1500);
    } catch (error) {
      setMessage({ type: 'error', text: 'Registration failed' });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (form, field, value) => {
    if (form === 'login') {
      setLoginData(prev => ({ ...prev, [field]: value }));
    } else {
      setRegisterData(prev => ({ ...prev, [field]: value }));
    }
  };

  const isLoginFormValid = () => {
    return loginData.email.trim() !== '' && loginData.password.trim() !== '';
  };

  const isRegisterFormValid = () => {
    const requiredFields = ['ownerName', 'email', 'password', 'phone'];
    const businessNameField = userType === 'retailers' ? 'businessName' : 'CompanyName';
    requiredFields.push(businessNameField);

    return requiredFields.every(field => registerData[field].trim() !== '');
  };

  return (
    <div className="h-[80vh] w-[80vw] mx-auto flex items-center justify-center">
      <div className="w-full h-full bg-white rounded-2xl shadow-xl flex items-center justify-center overflow-hidden font-eudoxus text-gray-900">

        {/* Left Panel */}
        <div className="flex-1 p-10 lg:flex-none lg:w-2/3 flex items-center justify-center">
          <div className="w-full max-w-xl">
            {/* Header */}
            <div className="text-center mb-10">
              <h1 className="text-5xl font-light mb-2">Business Portal</h1>
              <p className="text-gray-500 text-sm">Sign in to your account</p>
            </div>

            {/* User Type Selection */}
            <div className="flex mb-6 bg-gray-100 rounded-xl p-1 shadow-inner">
              {userTypes.map((type) => {
                const IconComponent = type.icon;
                return (
                  <button
                    key={type.id}
                    onClick={() => setUserType(type.id)}
                    className={`flex-1 py-2 px-3 text-sm font-medium rounded-lg transition-all flex items-center justify-center gap-1 ${
                      userType === type.id
                        ? 'bg-white text-gray-900 shadow-sm'
                        : 'text-gray-600 hover:text-gray-900'
                    }`}
                  >
                    <IconComponent className="w-4 h-4" />
                    {type.label}
                  </button>
                );
              })}
            </div>

            {/* Auth Tabs */}
            <div className="flex mb-6 border-b border-gray-200">
              <button
                onClick={() => {
                  setActiveTab('login');
                  setMessage({ type: '', text: '' });
                }}
                className={`flex-1 pb-2 text-base font-medium transition-colors ${
                  activeTab === 'login'
                    ? 'border-b-2 border-blue-500 text-blue-600'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                Sign In
              </button>
              <button
                onClick={() => {
                  setActiveTab('register');
                  setMessage({ type: '', text: '' });
                }}
                className={`flex-1 pb-2 text-base font-medium transition-colors ${
                  activeTab === 'register'
                    ? 'border-b-2 border-blue-500 text-blue-600'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                Create Account
              </button>
            </div>

            {/* Message */}
            {message.text && (
              <div className={`mb-4 p-3 text-sm rounded-lg ${
                message.type === 'success'
                  ? 'bg-green-50 text-green-700 border border-green-200'
                  : 'bg-red-50 text-red-700 border border-red-200'
              }`}>
                {message.text}
              </div>
            )}

            {/* Forms */}
            {/* ...LOGIN and REGISTER forms stay unchanged... */}

          </div>
        </div>

        {/* Right Panel with Image */}
        <div className="hidden lg:flex flex-1 items-center justify-center p-0">
          <img
            src="https://images.unsplash.com/photo-1556761175-4b46a572b786?auto=format&fit=crop&w=800&q=80"
            alt="Business Illustration"
            className="h-full w-full object-cover"
          />
        </div>

      </div>
    </div>
  );
}
