import React, { useRef, useEffect, useState } from "react";
import { motion } from "framer-motion";
import truckImg from "../assets/HomeTruck.png";

const TruckIntro = () => {
  const sectionRef = useRef(null);
  const [inView, setInView] = useState(false);

  // IntersectionObserver to trigger animation when in viewport
  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => setInView(entry.isIntersecting),
      { threshold: 0 }
    );
    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  const truckDuration = 4;

  return (
    <>
      <style>{`
        .truck-section {
          position: relative;
          width: 100%;
          height: 60px;
          min-height: 60px;
          background-color: #fff;
          overflow: hidden;
          display: flex;
          align-items: center;
          justify-content: flex-start;
          padding: 0;
        }
        .truck-bg {
          position: absolute;
          bottom: 0;
          left: 0;
          height: 60px;
          width: 0vw;
          background: linear-gradient(90deg, #90caf9 80%, #1976d2 100%);
          z-index: 0;
          pointer-events: none;
        }
        .truck {
          position: absolute;
          bottom: 8px;
          width: 48px;
          z-index: 2;
          filter: drop-shadow(0 2px 8px rgba(0,0,0,0.10));
        }
      `}</style>

      <div className="truck-section" ref={sectionRef}>
        {/* Animated blue background that grows with the truck */}
        <motion.div
          className="truck-bg"
          initial={{ width: "0vw" }}
          animate={inView ? { width: "100vw" } : { width: "0vw" }}
          transition={{
            width: { duration: truckDuration, ease: "easeInOut" }
          }}
        />

        {/* Truck animation from left to right */}
        <motion.img
          src={truckImg}
          alt="Truck"
          className="truck"
          initial={{ x: "-60px", opacity: 0 }}
          animate={
            inView
              ? { x: "calc(100vw - 60px)", opacity: 1 }
              : { x: "-60px", opacity: 0 }
          }
          transition={{ duration: truckDuration, ease: "easeInOut" }}
        />
      </div>
    </>
  );
};

export default TruckIntro;
