import React from "react";
import { motion } from "framer-motion";
import truckImg from "../assets/HomeTruck.png"; // Make sure this path is correct
import SplitText from "./SplitText"; // Your animated text component

const handleAnimationComplete = () => {
  console.log("All letters have animated!");
};

const TruckIntro = () => {
  return (
    <>
      <style>{`
        .truck-section {
          position: relative;
          width: 100%;
          min-height: 400px;
          background-color: #ffffff; /* No grey background */
          overflow: hidden;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          padding: 2rem 1rem;
        }

        .truck {
          position: absolute;
          bottom: 40px;
          width: 150px;
          z-index: 2;
        }

        .text-container {
          position: relative;
          z-index: 1;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          text-align: center;
          margin-top: 2rem;
        }
      `}</style>

      <div className="truck-section">
        {/* Truck animation from left to right */}
        <motion.img
          src={truckImg}
          alt="Truck"
          className="truck"
          initial={{ x: "-150px", opacity: 0 }}
          animate={{ x: "100vw", opacity: 1 }}
          transition={{ duration: 4, ease: "easeInOut" }}
        />

        {/* Animated text shows up after truck starts moving */}
        <motion.div
          className="text-container"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2.2, duration: 1.5, ease: "easeOut" }}
        >
          <SplitText
            text="Sledje"
            className="text-7xl md:text-7xl py-2 font-light text-black md:tracking-tight text-center"
            delay={80}
            duration={0.4}
            ease="power3.out"
            splitType="chars"
            from={{ opacity: 0, y: 40 }}
            to={{ opacity: 1, y: 0 }}
            onLetterAnimationComplete={handleAnimationComplete}
          />
          <SplitText
            text="Software Solutions"
            className="text-xl md:text-3xl font-light text-black md:tracking-tight text-center"
            delay={80}
            duration={0.4}
            ease="power3.out"
            splitType="chars"
            from={{ opacity: 0, y: 40 }}
            to={{ opacity: 1, y: 0 }}
            onLetterAnimationComplete={handleAnimationComplete}
          />
        </motion.div>
      </div>
    </>
  );
};

export default TruckIntro;
