import React, { useState } from "react";
import { Check, X, SendHorizonal, CircleCheck, History, Truck } from "lucide-react";

const DistributorOrders = () => {
  const [activeMode, setActiveMode] = useState("retailer"); // retailer | supplier
  const [activeTab, setActiveTab] = useState("requests"); // for retailer
  const [orderRequests, setOrderRequests] = useState([
    {
      id: 1,
      retailer: "FreshMart",
      items: [
        { name: "Aashirvaad Atta", quantity: 10, rate: 300 },
        { name: "Tata Salt", quantity: 5, rate: 20 },
      ],
    },
    {
      id: 2,
      retailer: "Kirana Plus",
      items: [{ name: "Fortune Oil", quantity: 4, rate: 160 }],
    },
  ]);
  const [acceptedOrders, setAcceptedOrders] = useState([]);
  const [onWayOrders, setOnWayOrders] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const [supplierHistory, setSupplierHistory] = useState([]);
  const [supplierOnWay, setSupplierOnWay] = useState([
    {
      id: 101,
      supplier: "BigBasket Wholesale",
      items: [
        { name: "Aashirvaad Atta", quantity: 20, rate: 280 },
        { name: "Tata Salt", quantity: 10, rate: 18 },
      ],
      expected: "Tomorrow",
    },
  ]);

  const acceptOrder = (orderId) => {
    const accepted = orderRequests.find((order) => order.id === orderId);
    setAcceptedOrders((prev) => [...prev, accepted]);
    setOrderRequests((prev) => prev.filter((order) => order.id !== orderId));
  };

  const rejectOrder = (orderId) => {
    setOrderRequests((prev) => prev.filter((order) => order.id !== orderId));
  };

  const sendOrder = (retailer) => {
    const sending = acceptedOrders.filter((order) => order.retailer === retailer);
    setOnWayOrders((prev) => [...prev, ...sending]);
    setAcceptedOrders((prev) => prev.filter((order) => order.retailer !== retailer));
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setActiveTab("requests");
  };

  const groupedByRetailer = (orders) => {
    return orders.reduce((acc, order) => {
      acc[order.retailer] = acc[order.retailer] || [];
      acc[order.retailer].push(order);
      return acc;
    }, {});
  };

  return (
    <div className="flex justify-between items-center mb-8 px-2">

      {/* Mode Toggle */}
      <div className="flex justify-center mb-8">
        <div className="inline-flex rounded-full p-1 transition duration-300"
        style={{
         backgroundColor: activeMode === "retailer" ? "#e0f2fe" : "#ccfbf1", // light blue or light teal
       }}>
          <button
            onClick={() => setActiveMode("retailer")}
            className={`px-6 py-2 rounded-full text-sm font-semibold transition duration-30 ${
              activeMode === "retailer" ? "bg-blue-600 text-white shadow" : "text-white"
            }`}
          >
            Retailer
          </button>
          <button
            onClick={() => setActiveMode("supplier")}
            className={`px-6 py-2 rounded-full text-sm font-semibold transition ${
              activeMode === "supplier" ? "bg-teal-600 text-white shadow" : "text-white"
            }`}
          >
            Supplier
          </button>
        </div>
      </div>

      {/* Retailer Section */}
      {activeMode === "retailer" && (
        <>
          {/* Tabs */}
          <div className="flex gap-6 border-b border-blue-500 mb-6">
            {["requests", "accepted", "onWay"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`text-center px-4 py-2 font-medium transition relative ${
                  activeTab === tab ? "text-blue-800 font-semibold" : "text-gray-600"
                }`}
              >
                {tab === "requests"
                  ? "Requests"
                  : tab === "accepted"
                  ? "Accepted"
                  : "On Way"}
                <div
                  className={`absolute bottom-0 left-0 right-0 h-1 ${
                    activeTab === tab ? "bg-blue-600" : "bg-transparent"
                  }`}
                />
              </button>
            ))}
          </div>

          {/* Requests */}
          {activeTab === "requests" &&
            (orderRequests.length === 0 ? (
              <p className="text-center text-gray-400">No new order requests.</p>
            ) : (
              orderRequests.map((order) => (
                <div key={order.id} className="bg-blue-50 rounded-lg p-4 mb-4 shadow-md">
                  <h3 className="text-xl font-bold text-blue-700">{order.retailer}</h3>
                  <ul className="mt-2">
                    {order.items.map((item, i) => (
                      <li key={i} className="text-gray-700">
                        {item.name} - Qty: {item.quantity}, Rate: ₹{item.rate}
                      </li>
                    ))}
                  </ul>
                  <div className="flex gap-4 mt-4">
                    <button
                      onClick={() => acceptOrder(order.id)}
                      className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 flex items-center gap-2"
                    >
                      <Check size={16} /> Accept
                    </button>
                    <button
                      onClick={() => rejectOrder(order.id)}
                      className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 flex items-center gap-2"
                    >
                      <X size={16} /> Reject
                    </button>
                  </div>
                </div>
              ))
            ))}

          {/* Accepted */}
          {activeTab === "accepted" &&
            (acceptedOrders.length === 0 ? (
              <p className="text-center text-gray-400">No accepted orders.</p>
            ) : (
              Object.entries(groupedByRetailer(acceptedOrders)).map(([retailer, orders], i) => {
                const total = orders
                  .flatMap((o) => o.items)
                  .reduce((sum, item) => sum + item.quantity * item.rate, 0);
                return (
                  <div
                    key={i}
                    className="bg-white shadow-md rounded-xl p-4 mb-6 border"
                  >
                    <div className="flex justify-between items-center mb-2">
                      <h3 className="text-xl font-bold text-blue-700">{retailer}</h3>
                      <button
                        onClick={() => sendOrder(retailer)}
                        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-800 flex items-center gap-2"
                      >
                        <SendHorizonal size={16} /> Ship
                      </button>
                    </div>
                    <ul className="space-y-2">
                      {orders.map((order) =>
                        order.items.map((item, i) => (
                          <li key={i} className="text-gray-700">
                            {item.name} - Qty: {item.quantity}, Rate: ₹{item.rate}
                          </li>
                        ))
                      )}
                    </ul>
                    <p className="mt-2 font-semibold text-right text-blue-800">
                      Total: ₹{total}
                    </p>
                  </div>
                );
              })
            ))}

          {/* On Way */}
          {activeTab === "onWay" &&
            (onWayOrders.length === 0 ? (
              <p className="text-center text-gray-400">No orders on the way.</p>
            ) : (
              onWayOrders.map((order, i) => (
                <div key={i} className="bg-blue-100 rounded p-4 mb-4 shadow">
                  <h4 className="text-lg font-semibold text-blue-800">{order.retailer}</h4>
                  <ul className="mt-2 text-gray-700">
                    {order.items.map((item, j) => (
                      <li key={j}>
                        {item.name} - Qty: {item.quantity}, Rate: ₹{item.rate}
                      </li>
                    ))}
                  </ul>
                </div>
              ))
            ))}
        </>
      )}

      {/* Supplier Section */}
      {activeMode === "supplier" && (
        <div className="space-y-6">
          {/* Orders On Way */}
          <div>
            <h2 className="text-xl font-bold text-blue-800 mb-2 flex items-center gap-2">
              <Truck size={20} /> Orders On Way
            </h2>
            {supplierOnWay.length === 0 ? (
              <p className="text-gray-400">No orders on the way.</p>
            ) : (
              supplierOnWay.map((order, i) => (
                <div key={i} className="bg-blue-50 rounded-lg p-4 shadow">
                  <h3 className="text-blue-900 font-bold text-lg mb-1">{order.supplier}</h3>
                  <p className="text-sm text-blue-600 mb-2">Expected: {order.expected}</p>
                  <ul className="text-sm text-gray-700">
                    {order.items.map((item, j) => (
                      <li key={j}>
                        {item.name} - Qty: {item.quantity}, Rate: ₹{item.rate}
                      </li>
                    ))}
                  </ul>
                </div>
              ))
            )}
          </div>

          {/* History */}
          <div>
            <h2 className="text-xl font-bold text-blue-800 mb-2 flex items-center gap-2">
              <History size={20} /> History
            </h2>
            {supplierHistory.length === 0 ? (
              <p className="text-gray-400">No history available.</p>
            ) : (
              supplierHistory.map((hist, i) => (
                <div key={i} className="bg-gray-100 rounded p-4 mb-2">
                  <p className="text-gray-700">{hist.description}</p>
                </div>
              ))
            )}
          </div>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md text-center">
            <CircleCheck size={48} className="text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-blue-800 mb-2">Order Dispatched!</h2>
            <p className="mb-6 text-gray-700">
              The order has been sent to the retailer successfully.
            </p>
            <button
              onClick={closeModal}
              className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default DistributorOrders;
