import React, { useRef, useEffect, useState } from "react";
import { motion } from "framer-motion";
import truckImg from "../assets/HomeTruck.png";
import SplitText from "./SplitText";

const handleAnimationComplete = () => {
  console.log("All letters have animated!");
};

const TruckIntro = () => {
  const sectionRef = useRef(null);
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        setInView(entry.isIntersecting);
      },
      { threshold: 0 }
    );
    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  // Animation timing
  const truckDuration = 6;
  const cloudDuration = truckDuration;
  const cloudDelay = 1.2; // Cloud appears after truck starts moving

  return (
    <>
      <style>{`
        .truck-section {
          position: relative;
          width: 100%;
          min-height: 400px;
          background-color: #ffffff;
          overflow: hidden;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          padding: 2rem 1rem;
        }

        .truck-bg {
          position: absolute;
          bottom: 0;
          left: 0;
          height: 100%;
          width: 0vw;
          background: linear-gradient(90deg, #e3f0fb 60%, #ffffff00 100%);
          z-index: 0;
          pointer-events: none;
          transition: background 0.3s;
        }

        .cloud-container {
          position: absolute;
          bottom: 90px;
          left: 0;
          width: 220px;
          height: 90px;
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2;
          pointer-events: none;
        }

        .cloud-shape {
          width: 220px;
          height: 90px;
          background: #fff;
          border-radius: 50% 50% 60% 60% / 60% 60% 80% 80%;
          box-shadow: 0 8px 32px 0 rgba(36,41,54,0.10);
          position: absolute;
        }

        .logo-in-cloud {
          position: relative;
          z-index: 2;
          font-size: 2rem;
          font-weight: 600;
          color: #1976d2;
          letter-spacing: 2px;
          font-family: 'Inter', 'Helvetica Neue', Arial, sans-serif;
        }

        .truck {
          position: absolute;
          bottom: 40px;
          width: 120px;
          z-index: 3;
          filter: drop-shadow(0 4px 16px rgba(0,0,0,0.10));
        }

        .text-container {
          position: relative;
          z-index: 4;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          text-align: center;
          margin-top: 2rem;
        }
      `}</style>

      <div className="truck-section" ref={sectionRef}>
        {/* Animated darker blue background that grows with the truck */}
        <motion.div
          className="truck-bg"
          style={{
            position: "absolute",
            bottom: 0,
            left: 0,
            height: "100%",
            width: "0vw",
            background: "linear-gradient(90deg, #90caf9 80%, #1976d2 100%)", // darker blue gradient
            zIndex: 0,
            pointerEvents: "none",
            transition: "background 0.3s"
          }}
          initial={{ width: "0vw" }}
          animate={
            inView
              ? { width: "100vw" }
              : { width: "0vw" }
          }
          transition={{
            width: { duration: truckDuration, ease: "easeInOut" }
          }}
        />

        {/* Large cloud covering half the background, moves with the blue background */}
        <motion.div
          className="cloud-big"
          style={{
            position: "absolute",
            left: 0,
            bottom: "50px",
            width: "50vw",
            height: "260px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1,
            pointerEvents: "none",
          }}
          initial={{ x: "-600px", opacity: 0 }}
          animate={
            inView
              ? { x: 0, opacity: 1 }
              : { x: "-600px", opacity: 0 }
          }
          transition={{
            x: { duration: truckDuration * 0.9, ease: "easeInOut" },
            opacity: { duration: 1.2, ease: "easeInOut" }
          }}
        >
          <div
            style={{
              width: "100%",
              height: "100%",
              background: "#fff",
              borderRadius: "60% 60% 70% 70% / 70% 70% 80% 80%",
              boxShadow: "0 12px 48px 0 rgba(36,41,54,0.13)",
              position: "absolute",
              left: 0,
              top: 0,
            }}
          />
          <div
            style={{
              position: "relative",
              width: "100%",
              textAlign: "center",
              zIndex: 2,
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              height: "100%",
            }}
          >
            <span style={{
              fontWeight: 800,
              fontSize: "4rem",
              color: "#1976d2",
              fontFamily: "'Inter', 'Helvetica Neue', Arial, sans-serif",
              textShadow: "0 2px 12px rgba(25, 118, 210, 0.08)",
              lineHeight: 1.1,
              letterSpacing: "2px"
            }}>
              SLEDGE
            </span>
            <span style={{
              fontWeight: 400,
              fontSize: "2rem",
              color: "#1976d2",
              fontFamily: "'Inter', 'Helvetica Neue', Arial, sans-serif",
              marginTop: "0.5rem",
              letterSpacing: "1px"
            }}>
              SOFTWARE SOLUTIONS
            </span>
          </div>
        </motion.div>

        {/* Truck animation from left to right, smooth and professional */}
        <motion.img
          src={truckImg}
          alt="Truck"
          className="truck"
          initial={{ x: "-220px", opacity: 0 }}
          animate={
            inView
              ? { x: "calc(100vw - 120px)", opacity: 1 }
              : { x: "-220px", opacity: 0 }
          }
          transition={{ duration: truckDuration, ease: "easeInOut" }}
        />

        {/* Animated text fades in after truck and cloud finish */}
        <motion.div
          className="text-container"
          initial={{ opacity: 0 }}
          animate={inView ? { opacity: 1 } : { opacity: 0 }}
          transition={{
            delay: truckDuration + 0.2,
            duration: 1.5,
            ease: "easeOut",
          }}
        >
          {/* You can remove this block if you only want the logo in the cloud */}
        </motion.div>
      </div>
    </>
  );
};

export default TruckIntro;
