import { motion, useAnimation, useInView } from "framer-motion";
import { useRef, useEffect, useState } from "react";
import { MessageCircle } from "lucide-react"; // if not already imported

const ExploreSection = () => {
  const controls = useAnimation();
  const ref = useRef(null);
  const inView = useInView(ref, { once: true });
  const [selectedCard, setSelectedCard] = useState(null);

  useEffect(() => {
    if (inView) {
      controls.start({
        opacity: 1,
        y: 0,
        transition: {
          duration: 0.8,
          type: "spring",
          stiffness: 80,
          damping: 20,
        },
      });
    }
  }, [inView, controls]);

  const cards = [
    {
      img: "https://www.apple.com/v/iphone/home/cb/images/overview/consider/apple_intelligence__gbh77cvflkia_large.jpg",
      title: "Shop",
    },
    {
      img: "https://www.apple.com/v/iphone/home/cb/images/overview/consider/camera__exi2qfijti0y_large.jpg",
      title: "Inventory",
    },
    {
      img: "https://www.apple.com/v/iphone/home/cb/images/overview/consider/privacy__ckc0wa30o55y_large_2x.jpg",
      title: "Orders",
    },
    {
      img: "https://www.apple.com/v/iphone/home/cb/images/overview/consider/safety__bwp7rsowtjiu_large.jpg",
      title: "Shelf",
    },
    {
      img: "https://www.apple.com/v/iphone/home/cb/images/overview/consider/apple_intelligence__gbh77cvflkia_large.jpg",
      title: "You",
    },
  ];

  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 100 }}
      animate={controls}
      className="w-full relative z-30 flex flex-col items-center pt-20"
    >
      <h2 className="text-5xl font-eudoxus font-bold tracking-tight mb-8 text-left w-full px-2">
        Explore Our Platform
      </h2>

      <div
        className="w-full h-[70vh] overflow-x-auto flex space-x-4 md:space-x-8 px-4 md:px-8 py-8"
        style={{
          scrollSnapType: "x mandatory",
          WebkitOverflowScrolling: "touch",
          overflowY: "hidden",
          overflowX: "auto",
        }}
      >
        {cards.map((card, idx) => {
          const isSelected = selectedCard === idx;

          return (
            <motion.div
              key={idx}
              className="flex-shrink-0 rounded-3xl shadow-lg flex items-center justify-center overflow-hidden relative cursor-pointer"
              onClick={() => setSelectedCard(isSelected ? null : idx)}
              initial={{ opacity: 0, y: 60 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{
                type: "spring",
                stiffness: 60,
                damping: 40,
                mass: 0.6,
              }}
              style={{
                width: isSelected ? "95vw" : "22vw",
                minWidth: isSelected ? "95vw" : "22vw",
                maxWidth: isSelected ? "95vw" : "22vw",
                height: "70vh",
                scrollSnapAlign: "start",
                zIndex: isSelected ? 20 : 1,
                transform: isSelected ? "scale(1.01)" : "none",
                transition: "all 0.5s ease-in-out",
              }}
            >
              <img
                src={card.img}
                alt={card.title}
                className="object-cover w-full h-full rounded-3xl"
                draggable={false}
              />

              {/* Overlay Text */}
              <div className="absolute top-0 left-0 p-5 z-10 text-white drop-shadow-lg">
                <div className="font-eudoxus text-3xl font-bold tracking-tight">
                  {card.title}
                </div>
                <div className="text-base font-medium text-blue-200">
                  {card.title} section
                </div>
              </div>

              {/* Description on Select */}
              {isSelected && (
                <div
                  style={{
                    position: "absolute",
                    bottom: "1.5rem",
                    left: "1.5rem",
                    right: "1.5rem",
                    zIndex: 4,
                    background: "rgba(255,255,255,0.75)",
                    padding: "1rem",
                    borderRadius: "1rem",
                    color: "#111",
                    backdropFilter: "blur(8px)",
                  }}
                >
                  <p>
                    This is the detailed description of the {card.title} section.
                  </p>
                </div>
              )}
            </motion.div>
          );
        })}
      </div>
    </motion.section>
  );
};

export default ExploreSection;
